CREATE DATABASE newhearings;
\c newhearings

-- 

CREATE table househearings (
  id BIGSERIAL NOT NULL PRIMARY KEY,
  title VARCHAR(255) NOT NULL,
  date DATE NOT NULL,
  time TIMESTAMP WITH TIME ZONE NOT NULL,
  location VARCHAR(255),
  link VARCHAR(255),
  text TEXT,
  committee CHAR(4) NOT NULL
);


\c postgres
